:root {
	--bg-buton: #f0f0f0;
	--border-button: solid 1px #888;
}

.button {
	font-size: 1.4em;
	background-color: var(--bg-buton);
	border: none;
	border-right: var(--border-button);
	border-bottom: var(--border-button);
	outline: none;
}

.button:active {
	background-color: #CCC;
}

.button.double {
	grid-column: span 2;
}

.button.triple {
	grid-column: span 3;
}

.button.operation {
	background-color: #FA8231;
	color: #FFF;
}

.button.button.operation:active {
	background-color: #FA8231cc;
}
